from get_aoc import get_input_lines

raw_input = get_input_lines(11)

total_steps = 100 # Question asks for 100

def starting_energy_levels (line): # Function to transform the input to int()
    energy_levels = []
    for number in line:
        energy_levels.append(int(number))
    return energy_levels

energy_levels = [starting_energy_levels(line) for line in raw_input] # Can I combine the function into this line?
print(energy_levels)

def add_one_to_all (energy_levels):
    return_levels = []
    for line in energy_levels:
        line_levels = []
        for octopus in line:
            line_levels.append(octopus + 1)
        return_levels.append(line_levels)
    #print("with one added")
    #print(return_levels)
    return return_levels

all_flashed = False # Checks to see if it needs to repeat. TO BE WORKED

def flash_octopus(octopus_line, octopus_row_number, energy_levels): # Where line and octopus are the row and column number, not the array entry (line, value)
    #print("Flashing " + str(octopus_line) + " , " + str(octopus_row_number))
    return_levels = [] # Blank array the will eventually be the new energy levels
    for line_number in range(len(energy_levels)): # Scrolling through lines
        line_levels = energy_levels[line_number] # That lines levels
        if line_number >= (octopus_line - 1) and line_number <= (octopus_line + 1): # If the line above, exact or below
            increased_line = [] # Blank line to be added to return_levels
            for octopus_number in range(len(line_levels)): # Scrolling through the row
                octopus = line_levels[octopus_number] # The value of that octo in the row
                if octopus_number >= (octopus_row_number - 1) and octopus_number <= (octopus_row_number + 1): # If to the left, above/below/, itself, or right
                    if line_number == octopus_line and octopus_number == octopus_row_number: # If the flashing octopus:
                        increased_line.append(octopus + 10) # Add ten to it so we know it's already flashed
                    else:
                        increased_line.append(octopus + 1) # Increases energy level of octo
                else:
                    increased_line.append(octopus) # Adds the original value to the line
            return_levels.append(increased_line) # Adds the increased energy line
        else: # If not one of the three concerned lines
            return_levels.append(line_levels) # Add the values as they were, unchanged
    return return_levels

flash_count = 0

def resets_flashed_to_zero(energy_levels):
    return_levels = []
    for line in energy_levels:
        line_levels = []
        for octopus in line:
            if octopus >= 20:
                line_levels.append(0)
            else: line_levels.append(octopus)
        return_levels.append(line_levels)
    #print("With Flashes reset")
    #print(return_levels)
    return return_levels

for step in range(total_steps): # Number of steps
    all_flashed = False
    print("Step " + str(step))
    energy_levels = add_one_to_all(energy_levels) # Adds one to each number in array
    while all_flashed == False: # CORRECTING
        #print("HERE HERE HERE")
        all_flashed = True
        for line_number in range(len(energy_levels)):
            line = energy_levels[line_number]
            for octopus_number in range(len(line)):
                octopus = line[octopus_number]
                if octopus >= 10 and octopus <20:
                    #print("Now here")
                    flash_count += 1
                    all_flashed = False
                    energy_levels = flash_octopus(line_number, octopus_number, energy_levels)
    print("Flash count is " + str(flash_count))
